DROP TABLE IF EXISTS `record`;

CREATE TABLE `record` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `create_date` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_check` int(11) DEFAULT '1' COMMENT '更新验证',
  `update_date` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  `log_user` varchar(50) DEFAULT NULL COMMENT '记录操作人',
  `rid` int(11) DEFAULT NULL COMMENT '简历ID',
  `rtitle` varchar(50) DEFAULT NULL COMMENT '简历名称',
  `rname` varchar(50) DEFAULT NULL COMMENT '求职人姓名',
  `rgender` varchar(2) DEFAULT NULL COMMENT '性别',
  `rdisabilitycategory` varchar(50) DEFAULT NULL COMMENT '残疾类别',
  `rage` int(3) DEFAULT NULL COMMENT '年龄',
  `reducation` varchar(50) DEFAULT NULL COMMENT '学历',
  `rmajor` varchar(50) DEFAULT NULL COMMENT '专业',
  `rschool` varchar(255) DEFAULT NULL COMMENT '毕业院校',
  `uid` int(10) DEFAULT NULL COMMENT '个人ID',
  `jid` int(11) DEFAULT NULL COMMENT '职位ID',
  `jname` varchar(255) DEFAULT NULL COMMENT '职位名称',
  `jsalary` varchar(50) DEFAULT NULL COMMENT '职位提供的薪水',
  `jdescription` varchar(500) DEFAULT NULL COMMENT '职位描述',
  `jcontacttel` varchar(50) DEFAULT NULL COMMENT '职位联系方式',
  `jcontactperson` varchar(50) DEFAULT NULL COMMENT '职位联系人',
  `jnature` varchar(50) DEFAULT NULL COMMENT '工作性质',
  `cid` int(10) DEFAULT NULL COMMENT '公司ID',
  `direction` tinyint(1) DEFAULT NULL COMMENT '投递方向标示符, true-简历投像职位, false-公司发送某职位的邀请到简历, 必填项, 任何增删改的操作都不得缺少!!!!!!!,默认为true',
  `comment` varchar(1000) DEFAULT NULL COMMENT '发送邀请说的话/求职留下的话',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=145 DEFAULT CHARSET=utf8;

