/*Table structure for table `user` */

DROP TABLE IF EXISTS `user`;

CREATE TABLE `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `create_date` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_check` int(11) DEFAULT '1' COMMENT '更新验证',
  `update_date` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  `log_user` varchar(50) DEFAULT NULL COMMENT '记录操作人',
  `login_name` varchar(50) NOT NULL COMMENT '用户名',
  `pass_word` varchar(50) NOT NULL DEFAULT '123' COMMENT '密码',
  `email` varchar(50) DEFAULT NULL COMMENT '邮箱',
  `phone` varchar(50) DEFAULT NULL COMMENT '联系电话',
  `identity` varchar(20) DEFAULT NULL COMMENT '账号类型:admin-管理员用户,individual-个人用户,company-公司用户,sa-超级管理员账号',
  `check_status` varchar(50) DEFAULT NULL COMMENT '审核状态',
  `authority` int(3) DEFAULT NULL COMMENT '权限： 999为管理员权限',
  `title` varchar(255) DEFAULT NULL COMMENT '个性标题名',
  `nick_name` varchar(50) DEFAULT NULL COMMENT '用户昵称',
  `head_title` varchar(50) DEFAULT NULL COMMENT '用户头像图片名',
  `head_image` mediumblob COMMENT '用户头像图片',
  `acode` varchar(10) DEFAULT NULL COMMENT '账号所属地地区外键',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=550 DEFAULT CHARSET=utf8;

