/*Table structure for table `work_experience` */

DROP TABLE IF EXISTS `work_experience`;

CREATE TABLE `work_experience` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `create_date` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_check` int(11) DEFAULT '1' COMMENT '更新验证',
  `update_date` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  `log_user` varchar(50) DEFAULT NULL COMMENT '记录操作人',
  `company_name` varchar(255) DEFAULT NULL COMMENT '公司名称',
  `job_name` varchar(255) DEFAULT NULL COMMENT '职位名称',
  `job_content` varchar(1000) DEFAULT NULL COMMENT '工作内容',
  `work_time` varchar(50) DEFAULT NULL COMMENT '在职时间',
  `leave_reason` varchar(255) DEFAULT NULL COMMENT '离职原因',
  `evaluation` varchar(1000) DEFAULT NULL COMMENT '评价',
  `rid` int(10) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=91 DEFAULT CHARSET=utf8;

