mapper.update:
update parameter 

	set value=#{value} 



		name=#{name} 


		and type=#{type} 



			and acode=#{area.code}

mapper.select:
select p.id as p_id, p.name as p_name, p.value as p_value, p.type as p_type, p.mark as p_mark,
 		a.code as a_code, a.name as a_name, a.pyname as a_pyName, a.abbr as a_abbr, a.mark as a_mark
from parameter as p, area as a 

	p.acode = a.code 

		and p.name = #{name}


		and p.value = #{value}


		and p.type = #{type}



			and p.acode = #{area.code}

select p.id as p_id, p.name as p_name, p.value as p_value, p.type as p_type, p.mark as p_mark,
 		a.code as a_code, a.name as a_name, a.pyname as a_pyName, a.abbr as a_abbr, a.mark as a_mark
from parameter as p, area as a 

	p.acode = a.code 

		and p.name = #{name}


		and p.value = #{value}


		and p.type = #{type}



			and p.acode = #{area.code}

select p.id as p_id, p.name as p_name, p.value as p_value, p.type as p_type, p.mark as p_mark,
 		a.code as a_code, a.name as a_name, a.pyname as a_pyName, a.abbr as a_abbr, a.mark as a_mark
from parameter as p, area as a 

	p.acode = a.code 


			and p.name = #{parameter.name}


			and p.type = #{parameter.type}


			and p.value = #{parameter.value}



				and acode = #{parameter.area.code}




order by id desc;
